var extension = function(swig, router)
{
    this.swig = swig;
    this.router = router;

    this.addFilters = function()
    {
        // Path filter
        var router = this.router;
        this.swig.setFilter("path", function(name, args){
            return router.generate(name, args);
        });
    }
}

module.exports = extension;