module.exports = function(swig, kernel)
{
    this.swig = swig;
    this.kernel = kernel;

    this.render = function(view, args)
    {
        var parts = view.split(":");
        var bundleName = parts[0];
        var viewRelativePath = parts[1];

        var bundle = this.kernel.getBundle(bundleName);

        var viewDirectory = bundle.directory + "/Resources/views/";
        var html = this.swig.renderFile(viewDirectory + viewRelativePath, args);

        return html;
    }
};