swig.loadExtensions = function(extensionChain)
{
    //console.log("-----");
    //console.log(this.container);
    //console.log("-----");
    var extensions = extensionChain.getExtensions();
    for(var i = 0; i < extensions.length; i++) {
        var extension = extensions[i];
        extension.addFilters();
    }
}

module.exports = swig;

